import { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import Data from "../Data/logements.json";
import Tag from "../components/Tag";
import Rating from "../components/Rating";
import Collapse from "../components/Collapse";
import Card from "../components/Card";
import Error from"../pages/Error";

function Houssing () {
    const {id}=useParams();
    const [data, setData]=useState()
    const [error, setError]= useState()

    function filtreData(datas){
        let filtreData
        filtreData =datas.filtre((d)=>d.id === id)
        if (filtreData.length === 0){
            setError(true)
        } else{
            return filtreData
        }
    }
    useEffect(()=>{
        async function getData() {
            try{
                const response =await fetch('../Data/logements.json')
                const datas = await response.json()
                setData(filtreData(datas))
            } catch (err){
                setError(true)
            }
        }
        getData()
    },[])
    if (error){
        return<Error/>

    } else if (data){
        return(
            <main>
                <article>
                < section>
                <div>
                    <div>
                        <h1>{data[0].titel}</h1>
                        <p>{data[0].location}</p>
                        <ul className='tag-container'>
                            {data[0].tags.map((tag,index)=>{
                                return(<li key={index} className='tag'>{tag}</li>)
                            })}
                        </ul>
                    </div>
                    <div className="estate-owner-container">

                        <Rating rating={data[0].rating}>
                        </Rating>
                    </div>
                </div>
            <div className="estate-collapsibles">
              <Collapse collapseTitle={'Description'}>
                <p>{data[0].description}</p>
              </Collapse>
              <Collapse collapseTitle={'Équipements'}>
                <ul>
                  {data[0].equipments.map((equipment, index) => {
                    return <li key={index}>{equipment}</li>
                  })}
                </ul>
              </Collapse>
            </div>
                    </section>
                </article>
            </main>
        )
    }

}

export default Houssing
