import React from "react";
import { useState, useRef, useEffect } from "react";
import{arrow} from "../assests/arrow down.png"

function Collapse(Propos){

    const [open, setOpen]=useState(true)
    const toggle =()=>{
        setOpen(!open)
    }
const contentRef = useRef()
return(
    <div className="collapse-container">
        <div className="collapse-title">
            <p>{Propos.collapseTitle}</p>
            <button
            aria-expanded={open ? 'true':'false'}
            aria-controls="collapse-parent"
            aria-label="open this collapse">
                <img
                src={arrow}
                className={open ? 'arrow down' : 'arrow up'}
                alt="Ouvrir l'article"
                onClick={toggle}/>
            </button>

        </div>
        <div
        id="collapse-parent"
        className={ open ? 'collapse-parent show': 'collapse-parent hide'}
        ref={contentRef}
        style={
            open ? {height: contentRef.current.scrollHeight + 'px'} 
            : {height :'0px'}
        }>
            <div className="collapse-content">{Propos.children}

            </div>

        </div>



    </div>
)
}
export default Collapse;
