import React, { useEffect, useState } from "react";
import { useParams,useNavigate } from "react-router-dom";
import Data from "../Data/logements.json";
import Tag from "../components/Tag";
import Rating from "../components/Rating";
import Collapse from "../components/Collapse";
import Card from "../components/Card";

function Houssing (){
    const {id}=useParams();
    const navigate= useNavigate;
    const [selecteData, setSelecteData]=useState(null);
    useEffect( () =>{
        const data =Data.find((item)=>item.id === id)
        if (!data){
            navigate('*');
        } else{
            setSelecteData(data);
        }
    },[id, navigate]);
    if (!selecteData){
        return null;
    }
    return(
        <>
        <header className="housing-header"> 
            <div className="carrousel-container">
                <Carrousel pictures={selecteData.pictures}/>
            </div>
        </header>
        <main className="houssing-main">
            <section className="info-container">
                <div className="title-container">
                    <h1>{selecteData.title}</h1>
                    <h2>{selecteData.location}</h2>
                    <div className="tag-container">
                        <Tag tags={selecteData.tags}></Tag>
                    </div>
                    <div className="rating-container">
                        <Card 
                        key={'${selecteData.id}-name'}
                        pictures={selecteData.host.pictures}
                        name={selecteData.host.name}
                        />
                    </div>

                </div>
            </section>
            <div className="houssing-collapse-container">
            <Collapse
                        key={`${selectedData.id}-equipments`}
                        title="Ã‰quipements"
                        content={
                            <ul>
                                {selectedData.equipments.map(
                                    (equipment, index) => (
                                        <li
                                            key={`${selectedData.id}-equipment-${index}`}
                                        >
                                            {equipment}
                                        </li>
                                    )
                                )}
                            </ul>
                        }
                    />
            </div>
        </main>
        </>
    )
}
export default Houssing
