import React, { useState, useEffect, useRef} from 'react';
import rightArrow from '../assets/arrow/arrow-right.png';
import leftArrow from '../assets/arrow/arrow-left.png';
import "../style/Carrousel.scss";



function Carrousel({ pictures }) {
  const [currentPicture, setCurrentPicture] = useState(0);
  const carrouselRef = useRef();

  const changePicture = (direction) => {
    if (direction === 'next') {
      setCurrentPicture((prevPicture) => (prevPicture + 1) % pictures.length);
    } else if (direction === 'previous') {
      setCurrentPicture((prevPicture) =>
        prevPicture === 0 ? pictures.length - 1 : prevPicture - 1
      );
    }
  };

  useEffect(() => {
    const handleSwipe = (e) => {
      const touchStartX = e.touches[0].clientX;

      const handleTouchMove = (e) => {
        const touchEndX = e.touches[0].clientX;
        const deltaX = touchStartX - touchEndX;

        if (deltaX > 50) {
          changePicture('next');
        } else if (deltaX < -50) {
          changePicture('previous');
        }
      };

      carrouselRef.current.addEventListener('touchmove', handleTouchMove);

      return () => {
        carrouselRef.current.removeEventListener('touchmove', handleTouchMove);
      };
    };

    carrouselRef.current.addEventListener('touchstart', handleSwipe);

    return () => {
      carrouselRef.current.removeEventListener('touchstart', handleSwipe);
    };
  }, [pictures.length]);

  return (
    <div className="carrousel-container" ref={carrouselRef}>
      <div className="carrousel-content">
        <img
          src={pictures[currentPicture]}
          alt={`Image ${currentPicture + 1} sur ${pictures.length}`}
          className="carrousel-image"
        />
        {pictures.length > 1 && (
          <p className="numbers">
            {currentPicture + 1}/{pictures.length}
          </p>
        )}
      </div>
    </div>
  );
}

export default Carrousel;
