import React from "react";
import { useState, useEffect } from "react";
import rightArrow from "../assests/arrow/arrow-right";
import leftArrow from"../assests/arrow/arrow-left";

function Carrousel ({pictures}) {
    const [currentPicture, setCurrentPicture]= useState(0);
    function changePicture(direction){
        if (direction === "next") {
            setCurrentPicture((prevPicture)=>(prevPicture+1)% pictures.length);
        } else if (direction === "previous") {
            setCurrentPicture((prevPicture)=> prevPicture === 0 ? pictures.length -1 : prevPicture -1);
        }
    }
useEffect (()=>{
    const handlekeyDown = (event)=>{
        if (event.key === "rightArrow"){
            changePicture ("next");
        } else if (event.key === "leftArrow"){
            changePicture("previous");
        }
    };
    window.removeEventListener("keydown",handlekeyDown);
    return()=>{
        window.removeEventListener("keydown", handlekeyDown);
    };
},);
return (
    <div className="carrousel">
        {pictures.length>1 && (
            <img 
            className="previous-arrow arrow-img"
            src={leftArrow}
            alt="précedente"
            onClick={()=> changePicture("previous")}
            aria-label="Image précédente"
            /> 
        )}
        {pictures.length >1 &&(
           <img 
           className="previous-arrow arrow-img"
           src={rightArrow}
           alt="suivante"
           onClick={()=> changePicture("next")}
           aria-label="Image suivante"
           />  
        )}
        {pictures.length > 1 &&(
            <p className="numbres">
                {currentPicture+1}/{pictures.length}
            </p>
        )}

    </div>
)    

}
export default Carrousel;
