import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Rating from '../components/Rating';
import Collapse from '../components/Collapse';
import Carrousel from '../components/Carrousel';
import Error from '../pages/Error';

function Housing() {
  const { id } = useParams();
  const [data, setData] = useState(null);
  const [error, setError] = useState(false);

  function filterData(datas) {
    const filteredData = datas.filter((d) => d.id === id);
    if (filteredData.length === 0) {
      setError(true);
      return null;
    } else {
      return filteredData;
    }
  }

  useEffect(() => {
    async function getData() {
      try {
        const response = await fetch('../Data/logements.json');
        const datas = await response.json();
        const result = filterData(datas);
        if (result) {
          setData(result);
        }
      } catch (err) {
        setError(true);
      }
    }
    getData();
  }, [id]);

  if (error) {
    return <Error />;
  } else if (data) {
    return (
      <main>
        <article>
          <section>
            <div>
              <div>
                <h1>{data[0].title}</h1>
                <p>{data[0].location}</p>
                <ul className="tag-container">
                  {data[0].tags.map((tag, index) => (
                    <li key={index} className="tag">
                      {tag}
                    </li>
                  ))}
                </ul>
              </div>
              <div className="Housing-owner-container">
                <Carrousel pictures={data[0].pictures} />
                <div>
                  <img src={data[0].host.picture} alt={data[0].host.name} />
                  <p>{data[0].host.name}</p>
                </div>
                <Rating rating={data[0].rating} />
              </div>
            </div>
            <div className="Housing-collapsibles">
              <Collapse collapseTitle="Description">
                <p>{data[0].description}</p>
              </Collapse>
              <Collapse collapseTitle="Ã‰quipements">
                <ul>
                  {data[0].equipments.map((equipment, index) => (
                    <li key={index}>{equipment}</li>
                  ))}
                </ul>
              </Collapse>
            </div>
          </section>
        </article>
      </main>
    );
  }
  return null;
}

export default Housing;
